const express = require('express');
const app = express();
const PORT = process.env.PORT || 3000; // Choose a port to listen on, ensure it's not port 5000

app.get('/', (req, res) => {
  res.send('Calculator App server is operational');
});

app.listen(PORT, () => {
  console.log(`Server running on port ${PORT}`);
});
