$(document).ready(function() {
  $('.calculator-keys button').on('click', function() {
    let buttonValue = $(this).val();
    let displayValue = $('#display').val();

    if (buttonValue === 'C') {
      $('#display').val('');
    } else {
      $('#display').val(displayValue + buttonValue);
    }
  });
});
